{
 "class": "ADC",
    "schemaVersion": "3.6.0",
    "id": "GSLB_Sample",
    "{{PARTITION_NAME}}": {
        "class": "Tenant",
        "{{VS2_NAME}}": {
            "class": "Application",
            "template": "generic",
            "testDomain": {
                "class": "GSLB_Domain",
                "domainName": "{{DNS2_NAME}}",
                "resourceRecordType": "A",
                "poolLbMode": "ratio",
                "pools": [
                              { "use": "{{VS2_NAME}}-pool" }
                ]
            },
            "{{VS2_NAME}}-pool": {
               "class": "GSLB_Pool",
                "enabled": true,
                "lbModeAlternate": "ratio",
                "lbModeFallback": "ratio",
                "manualResumeEnabled": false,
                "verifyMemberEnabled": true,
                "members": [
                	{
                	"ratio":10,
                	"server": {
                		"bigip": "/Common/bigip1.f5lab.local"
                		},
                		"virtualServer":"/{{PARTITION_NAME}}/{{PATH_NAME}}/{{VS1_NAME}}"
                	}
                ],
                "resourceRecordType": "A"
            }
        },
         "{{VS3_NAME}}": {
            "class": "Application",
            "template": "generic",
            "testDomain": {
                "class": "GSLB_Domain",
                "domainName": "{{DNS3_NAME}}",
                "resourceRecordType": "A",
                "poolLbMode": "ratio",
                "pools": [
                              { "use": "{{VS3_NAME}}-pool" }
                ]
            },
            "{{VS3_NAME}}-pool": {
               "class": "GSLB_Pool",
                "enabled": true,
                "lbModeAlternate": "ratio",
                "lbModeFallback": "ratio",
                "manualResumeEnabled": false,
                "verifyMemberEnabled": true,
                "members": [
                	{
                	"ratio":10,
                	"server": {
                		"bigip": "/Common/bigip1.f5lab.local"
                		},
                		"virtualServer":"/{{PARTITION_NAME}}/{{PATH_NAME}}/{{VS1_NAME}}"
                	}
                ],
                "resourceRecordType": "A"
            }
        }
    }
 
}
